mapper.select:
 
SELECT count(*)
FROM member
WHERE id = #{id}

 
SELECT count(*)
FROM member
WHERE id = #{id}
AND sns = #{sns}


SELECT id, pw
FROM member
WHERE id = #{id}
AND pw = #{pw}


SELECT id, sns
FROM member
WHERE id = #{id}
AND sns = #{sns}


SELECT id
FROM member
WHERE phone = #{phone}


SELECT *
FROM member
WHERE id = #{id}
AND name = #{name}

 
SELECT count(*)
FROM member
WHERE id = #{id}
AND pw = #{pw}

mapper.update:

UPDATE member
SET name = #{name}, gender = #{gender},
	phone = #{phone}, pw = #{newPw}, icon = #{icon},
	mms = #{mms}, mail = #{mail}, push = #{push}
WHERE id = #{id}
AND pw = #{pw}


UPDATE member
SET name = #{name}, gender = #{gender}, birth = #{birth}, phone = #{phone}
WHERE id = #{id}


UPDATE member
SET phone = #{phone}
WHERE id = #{id}

mapper.delete:

DELETE FROM member
WHERE id = #{id}
AND pw = #{pw}
